mapper.update:
update tb_catalog
set user_id = #{userId,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, name, pid
from tb_catalog
where id = #{id,jdbcType=INTEGER}

select id, user_id, name, pid
from tb_catalog
where pid = #{pid,jdbcType=INTEGER}
