mapper.update:
update tb_history
set time = #{time,jdbcType=BIGINT},
  record = #{record,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, time, record, user_id
from tb_history
where id = #{id,jdbcType=INTEGER}

select id, time, record, user_id
from tb_history
where user_id = #{userId,jdbcType=INTEGER}
