mapper.update:
update tb_message
set user_id = #{userId,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, title, content, status
from tb_message
where id = #{id,jdbcType=INTEGER}
